import React, { Component } from 'react';
import { Chart } from "chart.js";
import percentDiff from 'percentage-difference';
// import "./graph.css";
import { VictoryChart, VictoryVoronoiContainer, VictoryLine, VictoryGroup, VictoryTooltip, VictoryScatter    } from 'victory';

class Graph extends Component {

	//Declaring states
	state={
		zoomDomain: {
			x: [new Date(1990, 1, 1), new Date(2009, 1, 1)]
		}
	// 	regionalTrends: null,
	// 	stateTrends: null,
	// 	nationTrends: null,
	// 	yearsArr: []
	};

	handleZoom(domain) {
		this.setState({ zoomDomain: domain });
	}
	//Loads props after componented mounted
	componentDidMount(){

		//Calcuate percentage change for the following year
		const constcalculatesChange = array => {
			let diffsArr = [];
				for(let i=0; i < array.length; i++){
					diffsArr.push(percentDiff(array[0], array[i]))
				};

			// Return values in percent
			return(diffsArr);
		};

		//Generating year range for x-axis from start to end
		const labels = (start, end) => {
			let yearsArr = [];
			for(let i = start; i <= end; i++){
				yearsArr.push(i);
			};
			return(yearsArr);
		};

		//Shortening name
		let data = this.props.dataObj;
		// console.log(data);

		this.setState({

			//Trends
			regionalTrends: constcalculatesChange(data.trend_comparison.regional),
			stateTrends: constcalculatesChange(data.trend_comparison.state),
			nationTrends: constcalculatesChange(data.trend_comparison.nation),
			yearsArr: labels(data.summary.jobs_growth.start_year,data.summary.jobs_growth.end_year)

		});
	};

	render() {
		return (
			<VictoryChart height={500} width={1000}
				containerComponent={<VictoryVoronoiContainer />}
			>
				<VictoryGroup
					color="#c43a31"
					labels={({ datum }) => `y: ${datum.y}`}

					data={[
						{ x: 1, y: -3 },
						{ x: 2, y: 5 },
						{ x: 3, y: 3 },
						{ x: 4, y: 0 },
						{ x: 5, y: -2 },
						{ x: 6, y: -2 },
						{ x: 7, y: 5 }
					]}
				>
					<VictoryLine />
					<VictoryScatter
						size={({ active }) => active ? 8 : 3}
					/>
				</VictoryGroup>
				<VictoryGroup
					labels={({ datum }) => `y: ${datum.y}`}
					labelComponent={
						<VictoryTooltip
							style={{ fontSize: 10 }}
						/>
					}
					data={[
						{ x: 1, y: 3 },
						{ x: 2, y: 1 },
						{ x: 3, y: 2 },
						{ x: 4, y: -2 },
						{ x: 5, y: -1 },
						{ x: 6, y: 2 },
						{ x: 7, y: 3 }
					]}
				>
					<VictoryLine />
					<VictoryScatter
						size={({ active }) => active ? 8 : 3}
					/>
				</VictoryGroup>
			</VictoryChart>
		);
	}
};

export default Graph;
